export const messages = {
  en: {
    welcome: 'Welcome to Digital Guide. Choose an option like Send Money or Practice.',
    send_steps: [
      'Open your UPI app. Look for the Send Money option.',
      'Enter UPI ID or select contact. Choose who to send money to.',
      'Enter amount. Type the amount you want to send.',
      'Confirm and enter UPI PIN. Confirm details and enter your PIN.',
      'Success. Money sent successfully (practice only).'
    ],
  },
  hi: {
    welcome: 'डिजिटल गाइड में आपका स्वागत है। पैसे भेजना या प्रैक्टिस जैसे विकल्प चुनें।',
    send_steps: [
      'अपना UPI ऐप खोलें। Send Money विकल्प देखें।',
      'UPI ID दर्ज करें या संपर्क चुनें। जिसे पैसे भेजने हैं उसे चुनें।',
      'राशि दर्ज करें। जितनी राशि भेजनी है वह लिखें।',
      'कंफर्म करें और UPI पिन डालें। विवरण पक्का करें और पिन डालें।',
      'सफलता। पैसे सफलतापूर्वक (प्रैक्टिस) भेज दिए गए।'
    ],
  },
  te: {
    welcome: 'డిజిటల్ గైడ్‌కు స్వాగతం. Send Money లేదా Practice వంటి ఎంపికను ఎంచుకోండి.',
    send_steps: [
      'మీ UPI యాప్ ఓపెన్ చేయండి. Send Money ఎంపికను చూడండి.',
      'UPI ID ఇవ్వండి లేదా Contact ఎంచుకోండి. ఎవరికి పంపాలో ఎంచుకోండి.',
      'Amount ఎంటర్ చేయండి. పంపాల్సిన మొత్తం টাইప్ చేయండి.',
      'కన్‌ఫర్మ్ చేసి UPI PIN ఇవ్వండి. వివరాలు చూసి పిన్ ఎంటర్ చేయండి.',
      'విజయం. డబ్బు విజయవంతంగా పంపబడింది (ప్రాక్టీస్ మాత్రమే).'
    ],
  },
  ta: {
    welcome: 'டிஜிட்டல் கையேட்டுக்கு வரவேற்கிறோம். Send Money அல்லது Practice எனத் தேர்ந்தெடுக்கவும்.',
    send_steps: [
      'உங்கள் UPI செயலியைத் திறக்கவும். Send Money விருப்பத்தைப் பாருங்கள்.',
      'UPI ஐடி உள்ளிடவும் அல்லது தொடர்பைத் தேர்ந்தெடுக்கவும். யாருக்கு அனுப்ப வேண்டும் என்பதைத் தேர்ந்தெடுக்கவும்.',
      'தொகையை உள்ளிடவும். அனுப்ப வேண்டிய தொகையைத் type செய்யவும்.',
      'உறுதிசெய்து UPI PIN ஐ உள்ளிடவும். விவரங்களை சரிபார்த்து PIN கொடுக்கவும்.',
      'வெற்றி. பணம் வெற்றிகரமாக அனுப்பப்பட்டது (பயிற்சி மட்டும்).'
    ],
  },
};
