import React, { useEffect, useState } from 'react';
import { speak } from '../services/voice';
import { useLanguage } from '../contexts/LanguageContext';
import { t } from '../i18n';

function ZomatoFlow() {
  const { language } = useLanguage();
  const [step, setStep] = useState(0);
  const [search, setSearch] = useState('Pizza');
  const [items, setItems] = useState([]);
  const total = items.reduce((s,i)=>s+i.price,0);

  useEffect(()=>{ speak(t(language, 'k_choose_food_app'), language); },[language]);

  if (step === 0) return (
    <div className="p-4 space-y-4 fade-up">
      <h2 className="text-2xl font-bold">Zomato – Order Food</h2>
      <div className="card">
        <input value={search} onChange={e=>setSearch(e.target.value)} placeholder="Search dishes" />
        <div className="grid" style={{gridTemplateColumns:'1fr 1fr',gap:12,marginTop:12}}>
          {[
            {name:'Margherita Pizza',price:199},
            {name:'Veg Burger',price:129},
            {name:'Pasta Alfredo',price:179},
            {name:'Gulab Jamun',price:60},
          ].map(m => (
            <button key={m.name} className="card" onClick={()=>setItems(l=>[...l,m])}>
              <div className="font-bold">{m.name}</div>
              <div>₹ {m.price}</div>
            </button>
          ))}
        </div>
        <button className="btn" style={{marginTop:12}} onClick={()=>{ setStep(1); speak(t(language, 'k_review_cart_checkout'), language); }}>Cart ({items.length})</button>
      </div>
    </div>
  );

  if (step === 1) return (
    <div className="p-4 space-y-4 fade-up">
      <h2 className="text-2xl font-bold">Cart</h2>
      <div className="card">
        {items.length ? items.map((i,idx)=>(<div key={idx}>{i.name} – ₹ {i.price}</div>)) : 'No items yet'}
        <div className="font-bold" style={{marginTop:12}}>Total: ₹ {total}</div>
        <button className="btn" style={{marginTop:12}} onClick={()=>{ setStep(2); speak('Select address and pay', language); }}>Checkout</button>
      </div>
    </div>
  );

  if (step === 2) return (
    <div className="p-4 space-y-4 fade-up">
      <h2 className="text-2xl font-bold">Address & Payment</h2>
      <div className="card">
        <input placeholder="House no, street" style={{marginTop:12}} />
        <input placeholder="PIN code" style={{marginTop:12}} />
        <button className="btn" style={{marginTop:12}} onClick={()=>{ setStep(3); speak('Order placed', language); }}>Pay & Place Order</button>
      </div>
    </div>
  );

  return (
    <div className="p-4 space-y-4 fade-up">
      <h2 className="text-2xl font-bold">Success</h2>
      <div className="card">Order placed (simulated). Enjoy your meal!</div>
    </div>
  );
}

export default ZomatoFlow;
