import React from 'react';
import { useHistory } from 'react-router-dom';
import { speak } from '../services/voice';
import { useLanguage } from '../contexts/LanguageContext';

function BookTicketsSelect() {
  const history = useHistory();
  const { language } = useLanguage();

  React.useEffect(()=>{ speak('Choose ticket type: movie, train, or bus', language); }, [language]);

  return (
    <div className="p-4 space-y-4 fade-up">
      <h2 className="text-2xl font-bold">Choose Ticket Type</h2>
      <div className="grid grid-cols-2 gap-4">
        <button className="btn" onClick={()=>history.push('/book-tickets/movies')}>Movie Tickets</button>
        <button className="btn" onClick={()=>history.push('/book-tickets/train')}>Train Tickets</button>
        <button className="btn" onClick={()=>history.push('/book-tickets/bus')}>Bus Tickets</button>
        <button className="btn" onClick={()=>history.push('/book-tickets/flights')}>Flight Tickets</button>
      </div>
    </div>
  );
}

export default BookTicketsSelect;
