import React, { useEffect, useState } from 'react';
import { speak } from '../services/voice';
import { useLanguage } from '../contexts/LanguageContext';
import { messages } from '../i18n';

const steps = [
  { id: 1, title: 'Open your UPI app', desc: 'Look for the Send Money option.' },
  { id: 2, title: 'Enter UPI ID or select contact', desc: 'Choose who to send money to.' },
  { id: 3, title: 'Enter amount', desc: 'Type the amount you want to send.' },
  { id: 4, title: 'Confirm and enter UPI PIN', desc: 'Confirm details and enter your PIN.' },
  { id: 5, title: 'Success', desc: 'Money sent successfully (practice only).'},
];

function SendMoney() {
  const [current, setCurrent] = useState(0);
  const { language } = useLanguage();
  const localized = messages[language]?.send_steps || messages.en.send_steps;
  const step = steps[current];

  const next = () => setCurrent((c) => Math.min(c + 1, steps.length - 1));
  const prev = () => setCurrent((c) => Math.max(c - 1, 0));

  useEffect(() => {
    const langCode = language === 'te' ? 'te-IN' : language === 'hi' ? 'hi-IN' : language === 'ta' ? 'ta-IN' : 'en-IN';
    const text = localized[current] || `${step.title}. ${step.desc}`;
    speak(text, langCode);
  }, [current, step.id, step.title, step.desc, language]);

  return (
    <div className="p-4 space-y-4">
      <h2 className="text-2xl font-bold">Send Money Tutorial</h2>
      <div className="bg-white rounded-2xl p-4 shadow border">
        <div className="text-xl font-semibold">Step {step.id}: {step.title}</div>
        <div className="text-gray-700 mt-2 text-lg">{step.desc}</div>
        <div className="flex justify-between mt-4">
          <button onClick={prev} className="px-4 py-2 bg-gray-200 rounded-xl text-gray-900 font-semibold" disabled={current===0}>Back</button>
          <button onClick={next} className="px-4 py-2 bg-primary text-white rounded-xl font-semibold" disabled={current===steps.length-1}>Next</button>
        </div>
      </div>
    </div>
  );
}

export default SendMoney;
