import React, { useEffect, useState } from 'react';
import { speak } from '../services/voice';
import { useLanguage } from '../contexts/LanguageContext';

const APPS = [
  { id: 'phonepe', name: 'PhonePe' },
  { id: 'gpay', name: 'Google Pay' },
  { id: 'paytm', name: 'Paytm' },
];

function SendMoney() {
  const { language } = useLanguage();
  const [app, setApp] = useState(null);
  const [step, setStep] = useState(0);

  useEffect(() => {
    if (!app) speak('Choose an app for sending money', language);
  }, [app, language]);

  const startFlow = (selected) => {
    setApp(selected);
    setStep(0);
    speak(selected === 'phonepe' ? 'Tap Send Money to start' : 'Tap Pay to begin', language);
  };

  const phonepeFlow = [
    { t: 'Send Money', d: 'Big button to start', v: 'Tap the big Send Money button', cta: 'Send Money' },
    { t: 'Enter UPI or Mobile', d: 'Type number or UPI ID', v: 'Type the mobile or UPI ID', cta: 'Next' },
    { t: 'Confirm Recipient', d: 'Check name and photo', v: 'Check the name. Tap Confirm', cta: 'Confirm' },
    { t: 'Enter Amount', d: 'Use large keypad', v: 'Enter the amount', cta: 'Next' },
    { t: 'Summary', d: 'Review details and method', v: 'Review and tap Confirm', cta: 'Confirm' },
    { t: 'UPI PIN', d: 'Enter 4 or 6 digits (simulated)', v: 'Enter your UPI PIN', cta: 'Done' },
    { t: 'Processing', d: 'Simulating payment…', v: 'Please wait', cta: 'Continue' },
    { t: 'Success', d: 'Payment done. Save or share receipt', v: 'Payment successful', cta: 'Finish' },
  ];

  const gpayFlow = [
    { t: 'Pay', d: 'Choose Scan QR or Pay contact', v: 'Choose Scan QR or Pay contact', cta: 'Pay' },
    { t: 'Scan QR', d: 'Align the QR in the box', v: 'Align the QR code inside the box', cta: 'Simulate Scan' },
    { t: 'Confirm Merchant', d: 'Check merchant name', v: 'Check name then tap Confirm', cta: 'Confirm' },
    { t: 'Enter Amount', d: 'Type amount to pay', v: 'Type amount and tap Confirm', cta: 'Confirm' },
    { t: 'Choose Source', d: 'Pick account or card', v: 'Choose payment source', cta: 'Next' },
    { t: 'UPI PIN', d: 'Enter PIN to finish', v: 'Enter your UPI PIN', cta: 'Done' },
    { t: 'Success', d: 'Transaction done. Share receipt', v: 'Transaction done', cta: 'Finish' },
  ];

  const paytmFlow = [
    { t: 'UPI Pay', d: 'Send money using UPI', v: 'Tap UPI Pay', cta: 'UPI Pay' },
    { t: 'Enter Mobile', d: 'Enter number or pick contact', v: 'Enter mobile number', cta: 'Next' },
    { t: 'Confirm Recipient', d: 'Check name is correct', v: 'Check name and confirm', cta: 'Confirm' },
    { t: 'Enter Amount', d: 'Use keypad to type', v: 'Enter the amount', cta: 'Next' },
    { t: 'UPI PIN', d: 'Enter 4/6 digit PIN', v: 'Enter your UPI PIN', cta: 'Done' },
    { t: 'Success', d: 'Payment simulated', v: 'Payment successful', cta: 'Finish' },
  ];

  const flow = app === 'phonepe' ? phonepeFlow : app === 'gpay' ? gpayFlow : paytmFlow;

  useEffect(() => {
    if (!app) return;
    const s = flow[step];
    if (!s) return;
    const timer = setTimeout(() => speak(s.v, language), 150);
    return () => clearTimeout(timer);
  }, [app, step, language]);

  if (!app) {
    return (
      <div className="p-4 space-y-4 fade-up">
        <h2 className="text-2xl font-bold">Choose App for Send Money</h2>
        <div className="grid grid-cols-2 gap-4">
          {APPS.map(x => (
            <button key={x.id} className="btn" onClick={() => startFlow(x.id)}>{x.name}</button>
          ))}
        </div>
      </div>
    );
  }

  const s = flow[step];

  return (
    <div className="p-4 space-y-4 fade-up">
      <h2 className="text-2xl font-bold">{app === 'phonepe' ? 'PhonePe' : app === 'gpay' ? 'Google Pay' : 'Paytm'} – Send Money</h2>
      <div className="card">
        <div className="text-xl font-semibold">{s.t}</div>
        <div className="text-gray-700 mt-2 text-lg">{s.d}</div>
        <div className="flex justify-between mt-4">
          <button onClick={() => setStep(v => Math.max(v-1, 0))} className="px-4 py-2 bg-gray-200 rounded-xl text-gray-900 font-semibold" disabled={step===0}>Back</button>
          <button onClick={() => setStep(v => Math.min(v+1, flow.length-1))} className="px-4 py-2 btn" disabled={step===flow.length-1}>{s.cta}</button>
        </div>
      </div>
    </div>
  );
}

export default SendMoney;
