import React from 'react';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';

function formatRelative(dateLike) {
  try {
    const d = typeof dateLike === 'string' || typeof dateLike === 'number' ? new Date(dateLike) : (dateLike || new Date());
    const diffMs = Date.now() - d.getTime();
    const sec = Math.floor(diffMs / 1000);
    const min = Math.floor(sec / 60);
    const hr = Math.floor(min / 60);
    const day = Math.floor(hr / 24);
    if (sec < 45) return 'Just now';
    if (min < 60) return `${min} min${min === 1 ? '' : 's'} ago`;
    if (hr < 24) return `${hr} hour${hr === 1 ? '' : 's'} ago`;
    return `${day} day${day === 1 ? '' : 's'} ago`;
  } catch {
    return '';
  }
}

function loadLocalHistory() {
  try { return JSON.parse(localStorage.getItem('phonepe_history')||'[]'); } catch { return []; }
}
function loadLastSnap() {
  try { return JSON.parse(localStorage.getItem('phonepe_last_payment')||'null'); } catch { return null; }
}
function loadRecents() {
  try { return JSON.parse(localStorage.getItem('phonepe_recents')||'[]'); } catch { return []; }
}

export default function History() {
  const { token } = useAuth();
  const [items, setItems] = React.useState([]);
  const [q, setQ] = React.useState('');

  React.useEffect(() => {
    let cancelled = false;
    async function load() {
      try {
        if (token) {
          const resp = await api.getTransactions(token, 50);
          const mapped = (resp.items || []).map((it) => ({
            id: String(it._id || it.id),
            type: it.type,
            name: it.name,
            number: it.number,
            amount: it.amount,
            when: formatRelative(it.createdAt),
            status: it.status || (it.type === 'received' ? 'Credited to' : it.type === 'failed' ? 'Failed' : 'Debited from')
          }));
          // Merge with local fallback and last snap for immediate UX
          let base = mapped;
          const local = loadLocalHistory();
          if (!base.length && local.length) base = local;
          const last = loadLastSnap();
          if (last && !base.some(b => b.id === last.id)) base = [last, ...base];
          const recents = loadRecents().slice(0, 6).map((r, idx) => ({
            id: `recent_${idx}`,
            type: r.direction === 'received' ? 'received' : 'paid',
            name: r.name,
            amount: r.amount,
            when: 'today',
            status: r.direction === 'received' ? 'Credited to' : 'Debited from'
          }));
          if (!cancelled) setItems([...base, ...recents].slice(0, 50));
          return;
        }
      } catch (_) {}
      let base = loadLocalHistory();
      const last = loadLastSnap();
      if (!base.length && last) base = [last];
      const recents = loadRecents().slice(0, 6).map((r, idx) => ({
        id: `recent_${idx}`,
        type: r.direction === 'received' ? 'received' : 'paid',
        name: r.name,
        amount: r.amount,
        when: 'today',
        status: r.direction === 'received' ? 'Credited to' : 'Debited from'
      }));
      if (!cancelled) setItems([...base, ...recents].slice(0, 50));
    }
    load();
    return () => { cancelled = true; };
  }, [token]);

  const filtered = items.filter(tx => !q || (tx.name||'').toLowerCase().includes(q.toLowerCase()));

  return (
    <div className="p-4 space-y-4 fade-up">
      <h2 className="text-2xl font-bold">History</h2>
      <div className="card" style={{background:'#111827',padding:12,color:'#e5e7eb'}}>
        <div style={{display:'flex',alignItems:'center',gap:8}}>
          <div>üîç</div>
          <input value={q} onChange={e=>setQ(e.target.value)} placeholder="Search transactions" style={{flex:1,background:'transparent',border:'none',outline:'none',color:'#e5e7eb'}} />
          <div>‚öôÔ∏è</div>
        </div>
      </div>
      <div style={{display:'flex',flexDirection:'column',gap:12}}>
        {filtered.map(tx => (
          <div key={tx.id} className="card" style={{background:'#111827',color:'#e5e7eb',padding:12,display:'flex',gap:12,alignItems:'center'}}>
            <div style={{width:40,height:40,borderRadius:8,background: tx.type === 'received' ? '#10b981' : tx.type === 'failed' ? '#ef4444' : '#3b82f6',display:'flex',alignItems:'center',justifyContent:'center',color:'#fff',fontWeight:900}}>
              {tx.type === 'received' ? '‚Üì' : tx.type === 'failed' ? '!' : '‚Üë'}
            </div>
            <div style={{flex:1}}>
              <div style={{fontWeight:800}}>{tx.type === 'received' ? `Received from ${tx.name}` : tx.type === 'failed' ? `Payment to ${tx.name}` : `Paid to ${tx.name}`}</div>
              <div style={{display:'flex',gap:8,alignItems:'center'}}>
                <span style={{color:'#9ca3af',fontSize:12}}>{tx.when}</span>
                <span style={{color:'#9ca3af',fontSize:12}}>{tx.status}</span>
              </div>
            </div>
            <div style={{fontWeight:900}}>‚Çπ{tx.amount}</div>
          </div>
        ))}
        {!filtered.length && (
          <div className="card" style={{background:'#111827',color:'#9ca3af',padding:16}}>No transactions yet</div>
        )}
      </div>
    </div>
  );
}
