import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import LanguageSelector from './LanguageSelector';

const NavButton = ({ to, label }) => {
  const location = useLocation();
  const isActive = location.pathname === to;
  return (
    <Link
      to={to}
      className={`flex-1 text-center py-3 text-lg font-semibold ${
        isActive ? 'text-primary' : 'text-gray-500'
      }`}
      aria-label={label}
    >
      {label}
    </Link>
  );
};

function Layout({ children }) {
  const { user, logout } = useAuth();

  return (
    <div className="min-h-screen flex flex-col">
      <header className="sticky top-0 bg-white border-b max-w-md mx-auto w-full p-3 flex items-center justify-between">
        <Link to="/home" className="text-xl font-extrabold text-primary">Digital Guide</Link>
        <div className="flex items-center gap-3">
          <LanguageSelector />
          <div className="text-sm text-gray-600">
            {user?.name}
            <button onClick={logout} className="ml-2 text-primary text-xs">Logout</button>
          </div>
        </div>
      </header>
      <main className="flex-1 max-w-md mx-auto w-full">{children}</main>
      <nav className="sticky bottom-0 bg-white border-t max-w-md mx-auto w-full flex">
        <NavButton to="/home" label="Home" />
        <NavButton to="/practice" label="Practice" />
        <NavButton to="/help" label="Help" />
      </nav>
    </div>
  );
}

export default Layout;
